import React, { Component } from 'react';
import './style/card.css'

class Card extends React.Component{
    constructor(){
        super();
        this.state = {
            text: "",
            image: ""
        }
        this.updateText = this.updateText.bind(this);
    }

    updateText() {
     fetch(`https://deckofcardsapi.com/api/deck/new/draw/?count=1`)
        .then(response => response.json())
        .then(data => this.setState({
            text: data.cards[0].value+" of "+data.cards[0].suit,
            image: data.cards[0].image
        }))
        .catch(error => this.setState({ text: 'error fetching data' }))
    }

    render(){
        return(
            <div class="panel card">
                cards api
                <input onChange={this.updateText} type="text"></input>
                <div>
                {this.state.text}
                <image class="cardImage" src={this.state.image} alt={this.state.text} />
                </div>
            </div>
        )
    }
}

export default Card
