import React, { Component } from 'react';

class Country extends React.Component{

    constructor(){
        super();
        this.state = {
            text: "",
            country: ""
        }
        this.updateCity = this.updateCity.bind(this);
        this.updateText = this.updateText.bind(this);
        this.loadCity = this.loadCity.bind(this);
    }

    componentDidMount() {
           window.addEventListener('load', this.initialLoadCountry);
    }

    initialLoadCountry() {
     fetch('https://cors-anywhere.herokuapp.com/gd.geobytes.com/GetCityDetails')
        .then(response => response.json())
        .then(data => {this.loadCity(data.geobytescountry); return data})
        .then(data => this.setState({
                         country: data.geobytescountry
                     }))
        .catch(error => this.setState({ text: 'error fetching data '+error.message }))
    }

    loadCountry(value){
        fetch(`https://restcountries.eu/rest/v2/name/poland`)
         .then(response => response.json())
         .then(data => this.setState({
                 country: data[0].name,
             }))
         .catch(error => this.setState({ text: 'error fetching data '+error.message }))
    }

    render(){
        return(
            <div className="panel weather">
                country info
                <div>
                {this.state.country}
                </div>
            </div>
        )
    }
}

export default Country
