import React, { Component } from 'react';
import Slider from '@material-ui/core/Slider';
import './style/bored.css'

class Bored extends React.Component{
    constructor(){
        super();
        this.state = {
            text: "",
            accessibility: 0,
            type: "",
            participants: 1,
            price: 0
        }
        this.updateText = this.updateText.bind(this);
        this.handleChange1 = this.handleChange1.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.handleChange3 = this.handleChange3.bind(this);
    }

    componentDidMount() {
            window.addEventListener('load', this.updateText);
    }


    handleChange1 = (event, value) => {
        //console.log(value)
        this.setState({
            accessibility: value
        });
        this.updateText();
    }


    handleChange2 = (event, value) => {
        //console.log(value)
        this.setState({
            price: value
        });
        this.updateText();
    }


    handleChange3 = (event, value) => {
        //console.log(value)
        this.setState({
            participants: value
        });
        this.updateText();
    }

    updateText() {
          fetch(`http://www.boredapi.com/api/activity?price=${this.state.price}&participants=${this.state.participants}&accesibility=${this.state.accessibility}`)
          .then(response => response.json())
           .then(data => this.setState({
               text: data.activity
           }))
           .catch(error => this.setState({ text: 'error fetching data' }))
    }

    render(){
        return(
            <div class="panel bored">
                bored api
                <button onClick={this.updateText} type="text">Refresh</button>
                <div>
                    {this.state.text}
                </div>
             <Slider
                     onChange={this.handleChange1}
                     //valueLabelDisplay="auto"
                     aria-labelledby="continuous-slider"
                     step={0.01}
                     min={0}
                     max={1}
              /> {this.state.accessibility}
              <Slider
                onChange={this.handleChange2}
                aria-labelledby="continuous-slider"
                step={1}
                min={0}
                max={5}
              /> {this.state.price}
              <Slider
                onChange={this.handleChange3}
                aria-labelledby="continuous-slider"
                step={0.01}
                min={0}
                max={1}
              /> {this.state.participants}
              <div class="dropdown">
                <button class="dropbtn">Dropdown</button>
                <div class="dropdown-content">
                <a href="#">Link 1</a>
                <a href="#">Link 2</a>
                <a href="#">Link 3</a>
                </div>
              </div>
             </div>
            )
        }

}

export default Bored
