import React, { Component } from 'react';
import Slider from '@material-ui/core/Slider';

import './App.css';
import Norris from './Components/norris.js'
import Nameday from './Components/nameday.js'
import Numbers from './Components/numbers.js'
import Bored from './Components/bored.js'
import Card from './Components/card.js'
import Weather from './Components/weather.js'
import Country from './Components/country.js'
import Ip from './Components/ip.js'

class App extends Component {
    constructor(){
        super();
        this.state = {
           text: "",
           valueSlide: 0
        }
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(){
        const value = this.refs.slide1.value;
        this.setState(prevState => {
                return {
                  text: prevState,
                  valueSlide: value
                }
        });
        console.log(this.state.valueSlide)
    }

   render() {
      return (
        <div>
         <Slider
         onChange={this.handleChange}
         valueLabelDisplay="auto"
         aria-labelledby="continuous-slider"
         ref="slide1"
         />
            <Nameday />
            <Bored />
            <Numbers />
            <Norris />
            <Card />
            <Weather />
            <Country />
            <Ip />
        </div>
      );
    }
}


export default App;
