import React, { Component } from 'react';
import Slider from '@material-ui/core/Slider';
import './style/bored.css'

class Bored extends React.Component{
    constructor(){
        super();
        this.state = {
            text: "",
            returnAcc: "",
            returnTyp: "",
            returnPar: "",
            returnPri: "",
            accessibility: 0,
            type: "",
            participants: 1,
            price: 0
        }
        this.updateText = this.updateText.bind(this);
        this.handleChange1 = this.handleChange1.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.handleChange3 = this.handleChange3.bind(this);
    }

    componentDidMount() {
            window.addEventListener('load', this.updateText);
    }


    handleChange1 = (event, value) => {
        //console.log(value)
        this.setState({
            accessibility: value
        });
      //  this.updateText();
    }

    handleChange2 = (event, value) => {
        //console.log(value)
        this.setState({
            participants: value
        });
     //   this.updateText();
    }

    handleChange3 = (event, value) => {
        //console.log(value)
        this.setState({
            price: value
        });
     //   this.updateText();
    }

    handleCategory(){
        document.getElementById
    }

    updateText() {
          fetch(`http://www.boredapi.com/api/activity?maxprice=${this.state.price}&participants=${this.state.participants}&maxaccessibility=${this.state.accessibility}`)
          .then(response => response.json())
           .then(data => this.setState({
               text: data.activity,
               returnAcc: data.accessibility,
               returnTyp: data.type,
               returnPar: data.participants,
               returnPri: data.price,
           }))
           .catch(error => this.setState({ text: 'error fetching data' }))
    }

    render(){
        return(
            <div class="panel bored">
                bored api
                <button onClick={this.updateText} type="text">Refresh</button>
                <div>
                    activity: {this.state.text}<br />
                    accessibility: {this.state.returnAcc}<br />
                    type: {this.state.returnTyp}<br />
                    participants: {this.state.returnPar}<br />
                    price: {this.state.returnPri}<br />
                </div>
             <Slider
                     onChange={this.handleChange1}
                     //valueLabelDisplay="auto"
                     aria-labelledby="continuous-slider"
                     step={0.01}
                     min={0}
                     max={1}
              /> max accessibility: {this.state.accessibility}
              <Slider
                onChange={this.handleChange2}
                aria-labelledby="continuous-slider"
                step={1}
                min={1}
                max={5}
              /> no participants: {this.state.participants}
              <Slider
                onChange={this.handleChange3}
                aria-labelledby="continuous-slider"
                step={0.01}
                min={0}
                max={1}
              /> max price: {this.state.price}
              <div class="dropdown">
                <button class="dropbtn">Dropdown</button>
                <div class="dropdown-content">
                <a onClick={() => this.handleCategory("education")}>education</a>
                <a onClick={() => this.handleCategory("recreational")}>recreational</a>
                <a onClick={() => this.handleCategory("social")}>social</a>
                <a onClick={() => this.handleCategory("diy")}>diy</a>
                <a onClick={() => this.handleCategory("charity")}>charity</a>
                <a onClick={() => this.handleCategory("cooking")}>cooking</a>
                <a onClick={() => this.handleCategory("relaxation")}>relaxation</a>
                <a onClick={() => this.handleCategory("music")}>music</a>
                <a onClick={() => this.handleCategory("busywork")}>busywork</a>
                </div>
              </div>
             </div>
            )
        }

}

export default Bored
