import React, { Component } from 'react';
import './style/weather.css'

import location from './icons/location.png'
import cloud from './icons/cloud.svg'
import sun from './icons/sun.svg'
import cloudy_sun from './icons/cloudy_sun.svg'
import rain from './icons/rain.svg'
import snow from './icons/snow.svg'
import thunder from './icons/thunder_rain.svg'
import wind from './icons/wind.svg'
import wind_cloud from './icons/wind_cloud.svg'
import thunder_rain from './icons/thunder_rain.svg'

class Weather extends React.Component{

    constructor(){
        super();
        this.state = {
            text: "",
            city: "",
            country: "",
            temp: 0,
            icon: ""
        }
        this.updateCity = this.updateCity.bind(this);
        this.updateText = this.updateText.bind(this);
        this.loadCity = this.loadCity.bind(this);
    }

    componentDidMount() {
           window.addEventListener('load', this.updateCity);
           window.addEventListener('load', this.updateTime);
    }

    updateCity() {
     fetch('https://cors-anywhere.herokuapp.com/gd.geobytes.com/GetCityDetails')
        .then(response => response.json())
       // .then(data => console.log(data.geobytescity))
        /*.then(data => {this.setState({
            country: data.geobytescountry
        }); return data;})*/
        //.then(data => this.assignValue(data.geobytescity))
        .then(data => this.loadCity(data.geobytescity))
        .catch(error => this.setState({ text: 'error fetching data '+error.message }))
    }

    loadCity(value){
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${value}&appid=d0ca8d3b92279927c97dbc726daa6886`)
         .then(response => response.json())
         .then(data => {this.assignValue(data.name); return data;})
         .then(data => this.setState({
                 city: data.name,
                 text: data.weather[0].description,
                 country: data.sys.country,
                 temp: (data.main.temp-273.15).toFixed(1),
                 icon: data.weather[0].icon
             }))
         .catch(error => this.setState({ text: 'error fetching data '+error.message }))

this.weatherIcon();
    }

    updateText() {
         const value = this.refs.wea.value;
         fetch(`https://api.openweathermap.org/data/2.5/weather?q=${value}&appid=d0ca8d3b92279927c97dbc726daa6886`)
            .then(response => response.json())
            //.then(response => console.log(response))
            .then(data => this.setState({
                city: data.name,
                text: data.weather[0].description,
                country: data.sys.country,
                temp: (data.main.temp-273.15).toFixed(1),
                icon: data.weather[0].icon
            }))
            .catch(error => this.setState({ text: '' /*'error fetching data '+error.message*/ }))

	this.weatherIcon();

        }

	weatherIcon(){
	console.log(this.state.icon)

            switch(this.state.icon){
              case '01d':
document.getElementById("weaIcon").src={sun};
              break;
              case '01n':
document.getElementById("weaIcon").src={sun};
              break;
              case '02d':
document.getElementById("weaIcon").src={sun};
              break;
              case '02n':
document.getElementById("weaIcon").src={sun};
              break;
              case '03d':
document.getElementById("weaIcon").src={sun};
              break;
              case '03n':
document.getElementById("weaIcon").src={sun};
              break;
              case '04d':
document.getElementById("weaIcon").src={sun};
              break;
              case '04n':
document.getElementById("weaIcon").src={sun};
              break;
              case '09d':
document.getElementById("weaIcon").src={sun};
              break;
              case '09n':
document.getElementById("weaIcon").src={sun};
              break;
              case '010d':
document.getElementById("weaIcon").src={sun};
              break;
              case '010n':
document.getElementById("weaIcon").src={sun};
              break;
              case '011d':
document.getElementById("weaIcon").src={sun};
              break;
              case '011n':
document.getElementById("weaIcon").src={sun};
              break;
              case '013d':
document.getElementById("weaIcon").src={sun};
              break;
              case '013n':
document.getElementById("weaIcon").src={sun};
              break;
              case '50d':
document.getElementById("weaIcon").src={sun};
              break;
              case '50n':
document.getElementById("weaIcon").src={sun};
              break;
              default:
document.getElementById("weaIcon").src={sun};
            }
}

    assignValue(i){
         document.getElementById("wea").value=i;
    }

     removeValue(){
        document.getElementById("wea").value="";
    }

    updateTime(){
        let data = new Date();
        //Friday, 6 September
        let weekday = new Array(7);
        weekday[0] = "Sunday";
        weekday[1] = "Monday";
        weekday[2] = "Tuesday";
        weekday[3] = "Wednesday";
        weekday[4] = "Thursday";
        weekday[5] = "Friday";
        weekday[6] = "Saturday";
        const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

        document.getElementById("date").innerHTML=weekday[data.getDay()]+', '+data.getDate()+' '+ months[data.getMonth()];
    }

    render(){
        return(
            <div className="panel weather dark">
                <div>
                    <h1>{this.state.temp}°</h1>
                </div>
                <div>
                    <p id="date"></p>
                    <img src={location} /><input onChange={this.updateText} type="text" ref="wea" id="wea" onClick={this.removeValue}></input>
                    <p id="notice">click location if not applicable</p>
                </div>
                <div className="weaPic">
                  <img id="weaIcon" src='' />
                  <p>{this.state.text}</p>
                </div>
            </div>
        )
    }
}

export default Weather
